import React from 'react';
import RoomChatMain from '../components/room-chat-main';
import RoomChatInput from '../components/room-chat-input';

export default function Room(props) {
  const sendMessage = (message: string) => {
    console.log('send message', message);
    fetch('/api/message', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        roomname: props.roomname,
        username: props.username,
        message,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
      })
      .catch((err) => {
        console.error(`Something wrong happened while creating:${err.message}`);
      });
  };
  return (
    <div className="room-main">
      <RoomChatMain />
      <RoomChatInput sendMessage={sendMessage} />
    </div>
  );
}
