import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import CircleMenu from '../components/circle-menu';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    usernameInput: {
      width: '100%',
      color: 'rgb(66, 54, 48)',
      '& label.Mui-focused': {
        color: 'rgb(255, 192, 47)',
      },
      '& .MuiOutlinedInput-root': {
        '&:hover fieldset': {
          borderColor: 'rgb(255, 229, 0)',
        },
        '&.Mui-focused fieldset': {
          borderColor: 'rgb(255, 192, 47)',
        },
      },
    },
    createBtn: {
      backgroundColor: 'rgb(255, 229, 0)',
      borderRadius: '50px',
      fontSize: '11px',
      color: 'rgb(66, 54, 48)',
      width: '100px',
      border: '1px solid rgb(228, 228, 228)',
    },
    cancelBtn: {
      backgroundColor: 'white',
      borderRadius: '50px',
      fontSize: '11px',
      color: 'rgb(66, 54, 48)',
      width: '100px',
      border: '1px solid rgb(228, 228, 228)',
    },
  })
);

export default function EnterUsername(props: any) {
  const classes = useStyles();
  const [username, setUsername] = useState('');
  const [label, setLabel] = useState('Enter username');
  const [error, setError] = useState(false);

  const handleEnterBtnClick = () => {
    if (username.length < 1) {
      setError(true);
      setLabel('Please enter username');
    } else {
      setError(false);
      setLabel('Enter username');
      props.handleClose();
      props.createChatroom(username);
    }
  };

  return (
    <div className="enter-username-box">
      <div className="top">
        <CircleMenu
          handleExpand={props.handleExpand}
          handleMinimize={props.handleMinimize}
        />
      </div>
      <div>
        <TextField
          error={error}
          className={classes.usernameInput}
          id="outlined-basic"
          label={label}
          variant="outlined"
          size="small"
          value={username}
          onChange={(e: React.ChangeEvent<HTMLInputElement>) => {
            setUsername(e.target.value);
          }}
          autoComplete="off"
          inputProps={{ style: { fontSize: 12 } }}
          InputLabelProps={{ style: { fontSize: 12 } }}
        />
      </div>
    </div>
  );
}
