import React, { Component } from 'react';
import IndexPage from '../components/index-page';
import Main from '../components/main';
import Disclaimer from '../components/disclaimer';

export default class extends Component {
  constructor(props: any) {
    super(props);
    this.state = {
      isAcceptDisclaimer: localStorage.getItem('omegatalkaccept'),
    };
    this.setIsAcceptDisclaimer = this.setIsAcceptDisclaimer.bind(this);
  }

  setIsAcceptDisclaimer() {
    localStorage.setItem('omegatalkaccept', true);
    this.setState({
      isAcceptDisclaimer: true,
    });
  }
  render() {
    return (
      <>
        <IndexPage />
        <Main />
        {!this.state.isAcceptDisclaimer && (
          <Disclaimer setIsAcceptDisclaimer={this.setIsAcceptDisclaimer} />
        )}
      </>
    );
  }
}
