import React from 'react';

export default function RoomChatMain(props: any) {
  const convertDate = (dateAt: any) => {
    const today = new Date();
    const year = new Date(dateAt).getFullYear();
    const month = new Date(dateAt).getMonth();
    const day = new Date(dateAt).getDay();
    if (
      today.getFullYear() === year &&
      today.getMonth() === month &&
      today.getDay() === day
    ) {
      date = new Date(dateAt).toLocaleTimeString(navigator.language, {
        hour: '2-digit',
        minute: '2-digit',
      });
    }
    return date;
  };

  return (
    <div className="room-chat-main-box">
      {props.messages.map((message: any) => {
        console.log(message);
        return (
          <div key={message._id}>
            <div>{message.message}</div>
            <div>{message.username}</div>
            <div>{message.created}</div>
          </div>
        );
      })}
    </div>
  );
}
