import React, { useState, useRef } from 'react';
import { Rnd } from 'react-rnd';
import Left from '../components/left';
import ChatMain from '../components/chat-main';
import { useSpring, a, config } from 'react-spring';

export default function Main() {
  const [position, setPosition] = useState({ x: 200, y: 50 });
  const [tempPosition, setTempPosition] = useState({ x: 0, y: 0 });
  const [size, setSize] = useState({ width: 400, height: 640 });
  const [tempSize, setTempSize] = useState({ width: 400, height: 640 });
  let [isExpanded, setIsExpanded] = useState(false);
  let [isDraggable, setIsDraggable] = useState(true);

  const handleExpand = () => {
    if (isExpanded) {
      setPosition({ x: tempPosition.x, y: tempPosition.y });
      setSize({ width: tempSize.width, height: tempSize.height });
      setIsDraggable(true);
    } else {
      setTempPosition({ x: position.x, y: position.y });
      setPosition({ x: 0, y: 0 });
      setTempSize({ width: size.width, height: size.height });
      setSize({ width: window.innerWidth, height: window.innerHeight });
      setIsDraggable(false);
    }
    setIsExpanded(!isExpanded);
  };

  const handleMinimize = () => {
    if (isExpanded) {
      setPosition({ x: tempPosition.x, y: tempPosition.y });
      setSize({ width: tempSize.width, height: tempSize.height });
      setIsDraggable(true);
    } else {
      setTempPosition({ x: position.x, y: position.y });
      setPosition({ x: 0, y: 0 });
      setTempSize({ width: size.width, height: size.height });
      setSize({ width: window.innerWidth, height: window.innerHeight });
      setIsDraggable(false);
    }
    setIsExpanded(!isExpanded);
  };

  const height = window.innerHeight * 0.6;
  const draggingRef = useRef(true);
  const [{ y }, set] = useSpring(() => ({ y: height }));
  const myPos = 0;

  const open = ({ canceled }) => {
    set({ y: myPos, config: canceled ? config.wobbly : config.stiff });
  };
  const close = (velocity = 0) => {
    set({ y: height, config: { ...config.stiff, velocity } });
  };
  const bind = useDrag(
    ({ first, last, vxvy: [, vy], movement: [, my], cancel, canceled }) => {
      if (first) draggingRef.current = true;
      else if (last) setTimeout(() => (draggingRef.current = false), 0);
      if (last) my > height * 0.5 || vy > 0.25 ? open(vy) : close(vy);
      else set({ y: my, immediate: false, config: config.stiff });
    },
    {
      initial: () => [0, y.get()],
      filterTaps: true,
      bounds: { top: 10 },
      rubberband: true,
    }
  );
  const display = y.to((py) => 'block');
  open(true);

  return (
    <>
      <a.div style={{ display, bottom: `calc(100vh + ${100}px)`, y }}>
        <a.div
          {...bind()}
          onClick={() => !draggingRef.current && close()}
          className="drag-handle-box"
        >
          <Rnd
            default={{
              x: position.x,
              y: position.y,
              width: size.width,
              height: size.height,
            }}
            position={{
              x: position.x,
              y: position.y,
            }}
            size={{
              width: size.width,
              height: size.height,
            }}
            minWidth={320}
            minHeight={640}
            onDragStop={(e, d) => {
              setPosition({ x: d.x, y: d.y });
            }}
            onResizeStop={(e, direction, ref, delta, position) => {
              console.log(ref.style.width);
              setSize({
                width: Number(ref.style.width.split('px')[0]),
                height: Number(ref.style.height.split('px')[0]),
              });
            }}
            disableDragging={!isDraggable}
          >
            <div className="box">
              <div className="left">
                <Left
                  handleMinimize={handleMinimize}
                  handleExpand={handleExpand}
                />
              </div>
              <div className="main-chat">
                <ChatMain />
              </div>
            </div>
          </Rnd>
        </a.div>
      </a.div>
    </>
  );
}
