import React, { useState, useEffect } from 'react';
import RoomChatMain from '../components/room-chat-main';
import RoomChatInput from '../components/room-chat-input';
import socketIOClient from 'socket.io-client';
const socket = socketIOClient(':8081/');

export const initiateSocket = (room: any) => {
  console.log(`Connecting socket...`);
  if (socket && room) socket.emit('join', room);
};
export const disconnectSocket = () => {
  console.log('Disconnecting socket...');
  if (socket) socket.disconnect();
};
export const subscribeToChat = (cb: any) => {};

export default function Room(props: any) {
  const [messages, setMessages] = useState([]);
  const roommainRef = React.useRef<HTMLDivElement>(null);

  const sendMessage = (message: string) => {
    if (socket) {
      socket.emit('chat', {
        _id: props.window._id,
        roomname: props.window.roomname,
        username: props.username,
        message,
      });
    }
    // fetch('/api/message', {
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify({
    //     _id: props.window._id,
    //     roomname: props.window.roomname,
    //     username: props.username,
    //     message,
    //   }),
    // })
    //   .then((res) => res.json())
    //   .then((data: any) => {
    //     if (data.success) {
    //       setMessages([...messages, data.data] as any);
    //     }
    //   })
    //   .catch((err) => {
    //     console.error(
    //       `Something wrong happened while sending a message:${err.message}`
    //     );
    //   });
  };
  const getMessages = () => {
    fetch(`/api/getmessage`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        _id: props.window._id,
      }),
    })
      .then((res) => res.json())
      .then((data: any) => {
        if (data.success) {
          console.log(data);
          setMessages(data.data);
        }
      })
      .catch((err) => {
        console.error(
          `Something wrong happened while getting messages:${err.message}`
        );
      });
  };

  useEffect(() => {
    getMessages();
  }, []);

  useEffect(() => {
    const room = props.window._id;
    if (room) initiateSocket(room);
    if (!socket) return true;
    socket.on(`room-${room}`, (msg: any) => {
      console.log('Websocket event received!');
    });
    subscribeToChat((err: any, data: any) => {
      if (err) return;
      setMessages((messages: any) => [...messages, data.data] as any);
    });
    return () => {
      disconnectSocket();
    };
    // socket.on(`room-${props.window._id}`, (data: any) => {
    //   console.log('via socket', data.data);
    //   console.log(messages);
    //   const newMessages = [...messages, data.data];
    //   setMessages(newMessages as any);
    // });
  }, [props.window._id]);

  return (
    <div className="room-main" ref={roommainRef}>
      <RoomChatMain
        roommainHeight={roommainRef.current?.clientHeight}
        username={props.username}
        messages={messages}
      />
      <RoomChatInput sendMessage={sendMessage} />
    </div>
  );
}
