import React, { useState, useRef } from 'react';
import { Rnd } from 'react-rnd';
import Left from '../components/left';
import ChatMain from '../components/chat-main';
import { Keyframes, animated } from 'react-spring/renderprops';
import delay from 'delay';

const Minimize = Keyframes.Spring({
  peek: [
    { x: 0, from: { x: -100 }, delay: 500 },
    { x: -100, delay: 800 },
  ],
  open: { delay: 0, x: 0 },
  close: async (call) => {
    // await delay(400);
    await call({ delay: 0, x: -100 });
  },
});

export default function Main() {
  const [position, setPosition] = useState({ x: 200, y: 50 });
  const [tempPosition, setTempPosition] = useState({ x: 0, y: 0 });
  const [size, setSize] = useState({ width: 400, height: 640 });
  const [tempSize, setTempSize] = useState({ width: 400, height: 640 });
  let [isExpanded, setIsExpanded] = useState(false);
  let [isMinized, setIsMinized] = useState(false);
  let [isDraggable, setIsDraggable] = useState(true);

  const handleExpand = () => {
    if (isExpanded) {
      setPosition({ x: tempPosition.x, y: tempPosition.y });
      setSize({ width: tempSize.width, height: tempSize.height });
      setIsDraggable(true);
    } else {
      setTempPosition({ x: position.x, y: position.y });
      setPosition({ x: 0, y: 0 });
      setTempSize({ width: size.width, height: size.height });
      setSize({ width: window.innerWidth, height: window.innerHeight });
      setIsDraggable(false);
    }
    setIsExpanded(!isExpanded);
  };

  const handleMinimize = () => {
    if (isMinized) {
      setPosition({ x: position.x, y: tempPosition.y });
    } else {
      setTempPosition({ x: position.x, y: position.y });
      setPosition({ x: position.x, y: window.innerHeight - 50 });
    }
    setIsMinized(!isMinized);
  };

  return (
    <>
      <Rnd
        default={{
          x: position.x,
          y: position.y,
          width: size.width,
          height: size.height,
        }}
        position={{
          x: position.x,
          y: position.y,
        }}
        size={{
          width: size.width,
          height: size.height,
        }}
        minWidth={320}
        minHeight={640}
        onDragStop={(e, d) => {
          setPosition({ x: d.x, y: d.y });
        }}
        onResizeStop={(e, direction, ref, delta, position) => {
          console.log(ref.style.width);
          setSize({
            width: Number(ref.style.width.split('px')[0]),
            height: Number(ref.style.height.split('px')[0]),
          });
        }}
        disableDragging={!isDraggable}
      >
        <div className="box">
          <div className="left">
            <Left handleMinimize={handleMinimize} handleExpand={handleExpand} />
          </div>
          <div className="main-chat">
            <ChatMain />
          </div>
        </div>
      </Rnd>
    </>
  );
}
