import React from 'react';

export default function RoomChatMain(props: any) {
  const convertDate = (dateAt: any) => {
    const date = new Date(dateAt).toLocaleTimeString(navigator.language, {
      hour: '2-digit',
      minute: '2-digit',
    });
    return date;
  };

  return (
    <div className="room-chat-main-box">
      {props.messages.map((message: any) => {
        console.log(message);
        return (
          <div key={message._id} className="message-row">
            <div className="message-date">{convertDate(message.created)}</div>
            <div className="message-content">{message.message}</div>
            <div>{message.username}</div>
          </div>
        );
      })}
    </div>
  );
}
