import React, { useState, useEffect } from 'react';
import { Rnd } from 'react-rnd';
import Left from '../components/left';
import Top from '../components/top';
import ChatMain from '../components/chat-main';
import Room from '../components/room';
import EnterUsername from '../components/enter-username';
import Snackbar, { SnackbarOrigin } from '@material-ui/core/Snackbar';
import Tooltip from '@material-ui/core/Tooltip';

export interface State extends SnackbarOrigin {
  open: boolean;
}

export default function WindowMobile(props: any) {
  let element = null;
  switch (props.category) {
    case 'username':
      element = (
        <div className="username-box">
          <EnterUsername setUsername={props.setUsername} />
        </div>
      );
      break;
    case 'main':
      element = (
        <div className="main-box">
          <Left username={props.username} signout={props.signout} />
          <ChatMain openWindow={props.openWindow} username={props.username} />
        </div>
      );
      break;
    case 'chat-room':
      element = (
        <div className="room-box">
          <Top
            handleCloseWindow={handleCloseWindow}
            window={props.window}
            handleMinimize={handleMinimize}
            handleExpand={handleExpand}
          />
          <Room username={props.username} window={props.window} />
        </div>
      );
      break;
  }

  return (
    <>
      {!isMinimized && !isCloseMainWindow && (
        <Rnd
          default={{
            x: position.x,
            y: position.y,
            width: size.width,
            height: size.height,
          }}
          position={{
            x: position.x,
            y: position.y,
          }}
          size={{
            width: size.width,
            height: size.height,
          }}
          minWidth={320}
          minHeight={640}
          onDragStop={(e, d) => {
            setPosition({ x: d.x, y: d.y });
            props.setLastPosition({ x: d.x, y: d.y });
          }}
          onDragStart={() => {
            setZIndex(props.maxZIndex + 1);
            props.setMaxZIndex(() => props.maxZIndex + 1);
          }}
          onResizeStop={(e, direction, ref, delta, position) => {
            setSize({
              width: Number(ref.style.width.split('px')[0]),
              height: Number(ref.style.height.split('px')[0]),
            });
          }}
          disableDragging={!isDraggable}
          style={{ zIndex }}
        >
          {element}
        </Rnd>
      )}
      <Snackbar
        id="minimized"
        style={vertical === 'top' ? { marginLeft: 0 } : { marginLeft }}
        anchorOrigin={{ vertical, horizontal }}
        open={open}
        key={vertical + horizontal}
      >
        <>
          {isCloseMainWindow ? (
            <Tooltip title="open main window" arrow>
              <span
                onClick={() => openMainWindow()}
                className="closed-box bg-brown cursor-pointer"
              >
                <i className="far fa-comment"></i>
              </span>
            </Tooltip>
          ) : (
            <span className="minimized-box">
              <Left
                styleCategory="minimized"
                category={props.category}
                handleCloseWindow={handleCloseWindow}
                window={props.window}
                handleMinimize={handleMinimize}
                handleExpand={handleExpand}
              />
            </span>
          )}
        </>
      </Snackbar>
    </>
  );
}
