import React from 'react';
import { makeStyles, createStyles } from '@material-ui/core/styles';
import PermIdentityIcon from '@material-ui/icons/PermIdentity';

const useStyles = makeStyles(() =>
  createStyles({
    head: {
      margin: '13px',
      color: 'rgb(66, 54, 48)',
    },
    iconbox: {
      width: '50px',
    },
    icon: {
      color: 'rgb(203,214,242)',
      backgroundColor: 'rgb(160,182,233)',
      borderRadius: '30%',
      width: '40px',
      height: '40px',
    },
    room: {
      height: '30px',
      width: '100%',
      display: 'flex',
      flexDirection: 'row',
    },
    roomname: {
      paddingTop: '5px',
      width: '70%',
    },
    date: {
      color: 'rgb(173,173,173)',
      fontSize: '13px',
    },
  })
);

export default function ChatMainBody(props: any) {
  const classes = useStyles();
  const monthNames = [
    'Jan',
    'Feb',
    'Mar',
    'Apr',
    'May',
    'Jun',
    'Jul',
    'Aug',
    'Sep',
    'Oct',
    'Nov',
    'Dec',
  ];
  return (
    <div className={classes.head}>
      {props.rooms.map((room: any) => {
        console.log(room.updatedAt);
        const now = new Date();
        const month = monthNames[new Date(room.updatedAt).getMonth()];
        const day = new Date(room.updatedAt).getDay();
        console.log(day);
        return (
          <div key={room._id} className={classes.room}>
            <div className={classes.iconbox}>
              <PermIdentityIcon className={classes.icon} />
            </div>
            <div className={classes.roomname}>{room.roomname}</div>
            <div className={classes.date}>{`${month} ${day}`}</div>
          </div>
        );
      })}
    </div>
  );
}
