import React, { useState } from 'react';
import Window from '../components/window';

export default function Main() {
  const [maxZIndex, setMaxZIndex] = useState(0);
  const [windows, setWindows] = useState([] as any);
  const [lastPosition, setLastPosition] = useState({ x: 200, y: 50 });

  const openWindow = (_id: any, roomname: any) => {
    const newWindow = {
      _id,
      roomname,
      positionX: lastPosition.x + 20,
      positionY: lastPosition.y + 20,
      zIndex: maxZIndex + 1,
    };
    const newWindows = windows.filter((item: any) => {
      console.log('111', item._id);
      console.log('aaa', newWindow._id);
      item._id !== newWindow._id;
    });

    setMaxZIndex(maxZIndex + 1);
    setWindows([...windows, newWindow] as any);
    setLastPosition({ x: newWindow.positionX, y: newWindow.positionY });
  };
  return (
    <>
      <Window
        category="main"
        maxZIndex={maxZIndex}
        lastPosition={lastPosition}
        setMaxZIndex={setMaxZIndex}
        openWindow={openWindow}
      />
      {windows.map((window, index) => {
        console.log(window);
        return (
          <Window
            key={index}
            window={window}
            maxZIndex={maxZIndex}
            category="chat-room"
            setMaxZIndex={setMaxZIndex}
          />
        );
      })}
    </>
  );
}
