import React, { useState } from 'react';
import RoomChatMain from '../components/room-chat-main';
import RoomChatInput from '../components/room-chat-input';

export default function Room(props: any) {
  const [messages, setMessages] = useState([]);
  const sendMessage = (message: string) => {
    fetch('/api/message', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        owner: props.window.owner,
        roomname: props.window.roomname,
        username: props.username,
        message,
      }),
    })
      .then((res) => res.json())
      .then((data: any) => {
        console.log(data);
        if (data.success) {
          setMessages([...messages, data.messages] as any);
        }
      })
      .catch((err) => {
        console.error(
          `Something wrong happened while sending a message:${err.message}`
        );
      });
  };
  return (
    <div className="room-main">
      <RoomChatMain messages={messages} />
      <RoomChatInput sendMessage={sendMessage} />
    </div>
  );
}
